mapper.select:
select *
from `order`


        orderId=#{orderId}


        and userId=#{userId}


        and productId=#{productId}


        and orderState=#{orderState}


        and orderDate=#{orderDate}


        and orderMoney=#{orderMoney}



    limit #{startRow},#{num}

select count(*)
from `order`


        orderId=#{orderId}


        and userId=#{userId}


        and productId=#{productId}


        and orderState=#{orderState}


        and orderDate=#{orderDate}


        and orderMoney=#{orderMoney}

mapper.update:
update `order`
set orderState=#{newState}


        orderId=#{orderId}


        and userId=#{userId}


        and productId=#{productId}


        and orderState=#{orderState}


        and orderDate=#{orderDate}


        and orderMoney=#{orderMoney}
