        and `order`.productId=#{order.productId}


        and `order`.orderState=#{order.orderState}


        and `order`.orderDate=#{order.orderDate}


        and `order`.orderMoney=#{order.orderMoney}


        and orderGroupId=#{order.orderGroupId}

