mapper.select:
select *
from `shop`

    
        shopId=#{shopId}
    
    
        and userId=#{userId}
    
    
        and shopName=#{shopName}
    
    
        and shopIntro=#{shopIntro}
    
    
        and shopAddr=#{shopAddr}
    
    
        and shopRegisterFund=#{shopRegisterFund}
    
    
        and shopRegisterDate=#{shopRegisterDate}
    
    
        shopState=#{shopState}
    


    limit #{startRow},#{num}

select count(*)
from `shop`

    
        shopId=#{shopId}
    
    
        and userId=#{userId}
    
    
        and shopName=#{shopName}
    
    
        and shopIntro=#{shopIntro}
    
    
        and shopAddr=#{shopAddr}
    
    
        and shopRegisterFund=#{shopRegisterFund}
    
    
        and shopRegisterDate=#{shopRegisterDate}
    
    
        shopState=#{shopState}

mapper.update:
update `shop`

    
        shopName = #{shopName},
    
    
        shopIntro = #{shopIntro},
    
    
        shopAddr = #{shopAddr},
    
    
        shopRegisterFund = #{shopRegisterFund},
    
    
        shopState=#{shopState}
    


    
        userId=#{userId}
    
    
        and shopId=#{shopId}
