mapper.select:
select *
from `shop`
where shopIsOpen = #{shopIsOpen}
order by shopId
limit ${startRow}, ${number}

mapper.update:
update `shop`


        shopName = #{shopName},


        shopIntro = #{shopIntro},


        shopAddr = #{shopAddr},


        shopRegisterFund = #{shopRegisterFund},


        shopIsOpen=#{shopIsOpen}




        userId=#{userId}


        and shopId=#{shopId}
