mapper.select:
select *
from shoprecord


        shopRecordId=#{shopRecordId}


        and shopId=#{shopId}


        and shopRecordDate=#{shopRecordDate}


        and shopRecordComment=#{shopRecordComment}


        and shopRecordState=#{shopRecordState}



    limit #{startRow},#{num}

select count(*)
from shoprecord


        shopRecordId=#{shopRecordId}


        and shopId=#{shopId}


        and shopRecordDate=#{shopRecordDate}


        and shopRecordComment=#{shopRecordComment}


        and shopRecordState=#{shopRecordState}

mapper.update:
update shoprecord
set shopRecordState=#{newState}


        shopRecordId=#{shopRecordId}


        and shopId=#{shopId}


        and shopRecordDate=#{shopRecordDate}


        and shopRecordComment=#{shopRecordComment}


        and shopRecordState=#{shopRecordState}

update shoprecord


        and shopId=#{shopId}


        and shopRecordDate=#{shopRecordDate}


        and shopRecordComment=#{shopRecordComment}


        and shopRecordState=#{shopRecordState}


where shopRecordId=#{shopRecordId}
